import React from 'react';

function Condition() {
    
    let Greetings = '';
    let curtime = new Date().getHours();


    if (curtime >= 1 && curtime <= 12) {

        Greetings = 'Good Morning';

    } else if (curtime >= 12 && curtime <= 19) {

        Greetings = 'Good Afternoon';

    } else {

        Greetings = 'Good Night';
    }
    return Greetings;
}

export default Condition;
